INSERT INTO `news` VALUES ('203', '院长寄语', '<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	环球网报道：据法新社3月28日报道，俄罗斯总统普京28日祝贺俄罗斯军队在接管克里米亚过程中出色地完成了任务，称他们展现了俄军队的“新能力”。\r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	　　法新社援引了俄罗斯新闻机构报道称，普京表示，最近发生在克里米亚的事情对于俄罗斯军队而言是一次严峻考验，他们在这个过程中展示了俄罗斯军队“新能力”，以及崇高的道德精神。\r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	　　法新社称，这是普京首次承认俄罗斯军队直接参与接管克里米亚。\r\n</p>', 'admin', '2014-03-29 17:52:00', null, null, '19', '74', '16', '院长寄语', '学院概况', '0', '0', '0', '1');
